def part1(input: str) -> int:
    return sum([1 if x == '(' else -1 for x in input])


def test_part1():
    assert part1(")())())") == -3
    assert part1("))(((((") == 3


print("Part 1:", part1(input))


def part2(input: str) -> int:
    current_floor = 0
    for n, char in enumerate(input):
        if char == "(":
            current_floor += 1
        else:
            current_floor -= 1
        if current_floor < 0:
            return n+1


def test_part2():
    assert part2(")") == 1
    assert part2("()())") == 5


print("Part 2:", part2(input))
